DROP TABLE IF EXISTS full_guide_paragraphs;
DROP TABLE IF EXISTS full_guide_errors;
DROP TABLE IF EXISTS full_guides;
DROP TABLE IF EXISTS full_guides_meta;
DROP TABLE IF EXISTS route_map_points;
