INSERT INTO full_guides (guide_id, route_number, route_title, full_guide_url, canonical_url, article_title, date_published, date_modified, first_paragraph, guide_polyline_count, guide_point_count, scraped_at_utc) VALUES (2, 2, 'ROUTE 2 VILLA PLAZA TO CITY PROPER VIA CALUMPANG', 'https://shemaegomez.com/calaparan-calumpang-jeepney-route/', 'https://shemaegomez.com/calaparan-calumpang-jeepney-route/', 'Iloilo Jeep Route 2 Villa Plaza to City Proper via Calumpang Loop', '2024-10-30T02:16:31+00:00', '2026-01-07T02:27:36+00:00', 'Calaparan-Calumpang-Iloilo City Proper jeepney route is for those residing in coastal parts of Villa Arevalo from South Baluarte to Calaparan and Sto. NiÃ±o Norte. The end-to-end of this route is from Plaza Villa until Plaza Libertad.', 2, 525, '2026-02-18T11:57:13.507173+00:00');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (8, 2, 1, 'Calaparan-Calumpang-Iloilo City Proper jeepney route is for those residing in coastal parts of Villa Arevalo from South Baluarte to Calaparan and Sto. NiÃ±o Norte. The end-to-end of this route is from Plaza Villa until Plaza Libertad.');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (9, 2, 2, 'Yulo St. after Police Station, OsmeÃ±a St., Arroyo St. (Back of Arevalo Church), Bonifacio St., Yulo St. (Total Gasoline Station), Baluarte Calumpang-Villa-Oton Blvd., Rizal St. (Tanza Church), Ledesma St., Jalandoni St., De Leon St. (Super), Fuentes St., Ledesma St. (Robinsons City), Iznart St. (Socorro Drug), Rizal St. (lloilo Central Market), Ortiz St., JM Basa St. (Plaza Libertad/City Hall), Rizal St. (UI Phinma), Iznart St. (lloilo Central Market/Socorro Drug), Ledesma St. (Unitop/Robinsons City), Mabini St., De Leon St. (Super), Fuentes St., Ledesma St. (Colegio de las Hijas de Jesus), Rizal St., Infante St. (lloilo Fish Port), Baluarte-Calumpang-Villa-Oton BIvd., Total Gas Sta. (Yulo)');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (10, 2, 3, 'From Plaza Villa and Arevalo Elementary School, it will pass Yulo area covering Iloilo Supermart Villa, the public cemetery of Villa, RANHS up until Breakthrough restaurant. It will then pass straight to residential areas of Sto. NiÃ±o Norte, Calaparan, Calumpang, San Juan, Molo Boulevard and parts of South Baluarte (left side) and West Habog-Habog Molo (right side).');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (11, 2, 4, 'From Bangga Baluarte, it will make a right turn going to Iloilo Fishing Port.');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (12, 2, 5, 'Then make a right turn again going to Tanza Church.');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (13, 2, 6, 'If the jeepney is from Breakthrough going to the City Proper:');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (14, 2, 7, 'This route is passing Iloilo Terminal Market (â€œSuperâ€) to bring passengers who have transactions in the said area. If there is someone whose destination is Super, it will take a turn in the corner of Ledesma-Jalandoni. This is where the â€œfrontlineâ€ murals are located or a block after Tanza Church.');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (15, 2, 8, 'It will return to Ledesma Street passing the portion of Fuentes Street.');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (16, 2, 9, 'It will then go straight to Ledesma Street until it reaches Socorro Drug Store and will make a turn on the side of Iznart Street where the Chinese-Iloilo Friendship Arc is located.');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (17, 2, 10, 'It will go straight until it reaches Maria Clara Statue and turns to Rizal Street (University of Iloilo).');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (18, 2, 11, 'From Rizal Street, it will turn left to the extension of Ortiz Street the one going to Iloilo City Hall. It will make a right turn to the portion of JM Basa Street until it reaches the Mercury Drug Store on the corner and make a turn to Rizal Street passing Gaisano and, Iloilo Central Market passing back to the portion of Iznart Street from Iloilo Grand Hotel straight to Socorro Drug or Unitop area again.');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (19, 2, 12, 'Returning route:');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (20, 2, 13, 'It will pass Ledesma Street again until Infante back to the Baluarte area and go straight again until it reaches Breakthrough.');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (21, 2, 14, 'If the jeepney is from City Proper to going back to Breakthrough:');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (22, 2, 15, 'It will make a turn at the corner of Ledesma-Mabini (the side entrance of Robinsons Place Iloilo). Passing the streets of Mabini, a portion of J. De Leon and Fuentes until it can return to the main of Ledesma Street.');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (23, 2, 16, 'If you want to drop by Super, always tell the driver that your destination is there or else he will not pass this alternative route.');
