INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (46, 5, 1, 'The Route # 5 Festive Walk Transport Hub Iloilo City Proper via SM City is the â€œoldâ€ SM City Proper Route but in the new Iloilo Route, aside from SM City, it is now passing by the Atria Park District and entering Megaworld areas.');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (47, 5, 2, 'When the route first started, I find it really awkward because from the city, if you want to go to SM, you just have to drop by SM Strata and cross using the overpass (my gout). I am â€œyoungâ€ but my knees scream pain. How much more our senior mall-goers?');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (48, 5, 3, 'Then it will go straight to Megaworld. If you want to be on the main entrance of Megaworld, you will have to go to Megaworld first (which means higher fair because of there were more kilometers travelled).');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (49, 5, 4, 'But this was at the birth of LPTRP.');
INSERT INTO full_guide_paragraphs (id, guide_id, paragraph_order, paragraph_text) VALUES (50, 5, 5, 'Now, the Route # 5 Festive Walk Transport Hub Iloilo City Proper via SM City is way better. You can now drop by SM Cityâ€™s main entrance from the city proper areas eliminating the gruesome â€˜hikingâ€™ at the overpass.');
